mapper.select:
SELECT
availabilityId FROM selected_availabilites WHERE submissionId =
#{submissionId}

mapper.update:
UPDATE resume_sections SET

		consultant_rating = #{section.cmRating}
		 hiring_manager_rating = #{section.hmRating}

	WHERE id = #{section.sectionId}

update.foreach:
UPDATE resume_sections SET

		consultant_rating = #{section.cmRating}
		, hiring_manager_rating = #{section.hmRating}

	WHERE id = #{section.sectionId}
